import numpy as np #to convert list to numpy array
import matplotlib.pyplot as plt #to plot the array

arecibo_list = []

#arecibo signal as string
data = ''

#breaking characters to string
for i in data:
    arecibo_list.append(int(i))

#convert list to array
arecibo_array = np.array(arecibo_list)

#reshape array to form image width and height
arecibo_image = arecibo_array.reshape(73, 23)

#ploting array
plt.imshow(arecibo_image, cmap="binary")
plt.axis("off")
plt.show()